import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        // Toggle the 'sb-sidenav-toggled' class on the body when the sidebar status changes
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    return (
        <Router>
            <div id="layoutSidenav" className={`sb-sidenav-toggled-${isSidebarOpen}`}>
                <SideBar />
            </div>
            <div id="layoutMain">
                {!isSidebarOpen && <NavBar toggleSidebar={toggleSidebar} />}
                <Routes>
                    <Route path="/" element={<MainBar />} />
                </Routes>
            </div>
            <div className="bg-primary">
                <div id="layoutAuthentication">
                    <Routes>
                        <Route path="/auth" element={<Auth />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
