import React, { useState, useEffect } from 'react';
import DoughnutChart from './DoughnutChart'; // Import the DoughnutChart component
import Table from './Table'; // Import the Table component
import Button from './Button'; // Import the Button component

const Task = ({ userId }) => {
  const [tasks, setTasks] = useState([]);

  const handleDelete = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3001/deleteTask/${userId}/${taskId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // If the task was deleted successfully, update the state to remove the task
        setTasks(tasks.filter(task => task._id !== taskId));
      } else {
        // Handle error if the task was not deleted
        console.error('Failed to delete the task');
      }
    } catch (error) {
      console.error('Error deleting the task:', error);
    }
  };

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`http://localhost:3001/tasks/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch tasks');
        }
        const data = await response.json();
        setTasks(data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [userId]);

  return (
    <div>
      <h3>Tasks</h3>
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task._id}>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Task;
