import { useState, useEffect } from 'react';

const useContent = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(() => {
        // Get the authentication status from localStorage if available
        const auth = localStorage.getItem('isAuthenticated');
        return auth ? JSON.parse(auth) : false;
    });

    const handleLogin = async (email, password) => {
        try {
            const response = await fetch("http://localhost:3001/signin", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });
    
            if (response.ok) {
                setIsAuthenticated(true);
                localStorage.setItem('isAuthenticated', true); // Store authentication status in localStorage
            } else {
                setIsAuthenticated(false);
                localStorage.removeItem('isAuthenticated'); // Remove if the login fails
                // You might want to show an error message here
            }
        } catch (error) {
            console.error("Error:", error);
        }
    };
    

    const handleLogout = () => {
        setIsAuthenticated(false);
        localStorage.removeItem('isAuthenticated'); // Remove the authentication status on logout
    };

    useEffect(() => {
        const storedIsAuthenticated = localStorage.getItem('isAuthenticated');
        if (storedIsAuthenticated && JSON.parse(storedIsAuthenticated) === true) {
            setIsAuthenticated(true);
        }
    }, []);

    return { isAuthenticated, handleLogin, handleLogout };
};

export default useContent;
