import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import DoughnutChart from '../Dashboard/DoughnutChart';

function Task({ userId }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`http://localhost:3001/${userId}/stasks`);
        const data = await response.json();
        setTasks(data.stasks || []);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [userId]);

  const handleSubmit = async () => {
    const newTask = {
      userId: userId, // Associate the task with the user by ID
      // other task details...
    };

    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        // If submission is successful, fetch the updated tasks
        const updatedResponse = await fetch(`http://localhost:3001/${userId}/stasks`);
        const updatedData = await updatedResponse.json();
        setTasks(updatedData.stasks || []);
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3001/deleteTask/${userId}/${taskId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        const updatedTasks = tasks.filter(task => task._id !== taskId);
        setTasks(updatedTasks);
      } else {
        console.error('Task deletion failed');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <Button onClick={handleSubmit}>Submit Task</Button>
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
