import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import DoughnutChart from '../Dashboard/DoughnutChart';

function Task() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      // Fetch tasks based on the currently logged-in user
      const userId = localStorage.getItem('userId');
      const response = await fetch(`http://localhost:3001/stasks/${userId}`);
      const data = await response.json();
      setTasks(data.stasks);
    };

    fetchTasks();
  }, []);

  const handleDelete = async (taskId) => {
    try {
      const userId = localStorage.getItem('userId');
      await fetch(`http://localhost:3001/deleteTask/${userId}/${taskId}`, {
        method: 'DELETE',
      });
      setTasks(tasks.filter((task) => task._id !== taskId));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
