import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

function Task() {
  const [tasks, setTasks] = useState([]);
  const [newTaskName, setNewTaskName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newTaskStatus, setNewTaskStatus] = useState('');
  const [newCompletedOn, setNewCompletedOn] = useState('');
  const [newLink, setNewLink] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Function to fetch tasks
    const fetchTasks = async () => {
      const response = await fetch('http://localhost:3001/users/1/stasks'); // Replace '1' with the batch or user ID
      const data = await response.json();
      setTasks(data.stasks);
    };

    fetchTasks();
  }, []);

  const handleDelete = async (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);

    try {
      await fetch(`http://localhost:3001/tasks/${tasks[index].id}`, {
        method: 'DELETE',
      });
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddTask = async () => {
    try {
      const response = await fetch('http://localhost:3001/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          taskName: newTaskName,
          description: newDescription,
          status: newTaskStatus,
          completedOn: newCompletedOn,
          link: newLink,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setTasks([...tasks, data.newTask]);
        setNewTaskName('');
        setNewDescription('');
        setNewTaskStatus('');
        setNewCompletedOn('');
        setNewLink('');
      } else {
        console.error('Task creation failed');
      }
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div>
      <h1>Tasks</h1>
      <form onSubmit={(e) => { e.preventDefault(); handleAddTask(); }}>
        {/* Input fields for new task */}
        {/* ... (Task Name, Description, Status, Completed On, and Link inputs) */}
        <Button type="submit">Add Task</Button>
      </form>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th></th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Completed On</th>
            <th>Link</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.completedOn}</td>
              <td>{task.link}</td>
              <td>
                <Button variant="primary" onClick={() => navigate(`/edit-task/${index}`)}>
                  Edit
                </Button>{' '}
                <Button variant="danger" onClick={() => handleDelete(index)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
