import React, { Component } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css';
import './App.css';
import SideBar from "./component/Side_bar/SideBar";
import MainBar from "./component/Dashboard/MainBar";
import Login from "./component/Login";
import Register from "./component/Register";
import ForgetPs from "./component/ForgetPs";
import MyChart from "./component/Linechart/MyChart";
import BarChart from "./component/Task_status/Task_status_api";
import Dashboard from "./component/Dashboard/Dashboard";
import Task from "./component/Tasks/Task_api";
import Dclass from "./component/Side_bar/Dclass";
import Syllubus from "./component/Side_bar/Syllubus";

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isSidebarOpen: false,
            isLoggedIn: false,
            users: [
                {
                    id: 1,
                    name: 'John Doe',
                    email: 'john@gmail.com',
                    password: 'password',
                }
            ] 
        };
    }

    // Simulating user registration
    handleRegistration = (userData) => {
        const { users } = this.state;
        users.push(userData);
        this.setState({ users });
    };

    // Simulating user login
    handleLogin = (email, password) => {
        const { users } = this.state;
        const user = users.find(user => user.email === email && user.password === password);
        if (user) {
            this.setState({ isLoggedIn: true });
            // You might want to store user info in localStorage or manage tokens in a real app
        } else {
            alert("Invalid credentials");
        }
    };

    toggleSidebar = () => {
        this.setState({ isSidebarOpen: !this.state.isSidebarOpen });
    };

    componentDidMount() {
        document.body.classList.toggle('sb-nav-fixed', true);
        // Simulating user data storage in local storage
        const storedUsers = JSON.parse(localStorage.getItem("users")) || [];
        this.setState({ users: storedUsers });
    }

    componentDidUpdate(_, prevState) {
        // Simulating user data storage in local storage
        if (prevState.users !== this.state.users) {
            localStorage.setItem("users", JSON.stringify(this.state.users));
        }

        if (prevState.isSidebarOpen !== this.state.isSidebarOpen) {
            document.body.classList.toggle('sb-sidenav-toggled', this.state.isSidebarOpen);
        }
    }

    shouldShowSidebar = () => {
        return !['/login', '/register', '/forgetps'].includes(window.location.pathname);
    };

    shouldShowNavBar = () => {
        return !window.location.pathname.includes('/login') &&
            !window.location.pathname.includes('/register') &&
            !window.location.pathname.includes('/forgetps');
    };

    render() {
        const { isLoggedIn } = this.state;

        return (
            <Router>
                {this.shouldShowNavBar() && (
                    <nav>
                        <NavBar toggleSidebar={this.toggleSidebar} />
                    </nav>
                )}

                <div id="layoutSidenav">
                    {this.shouldShowSidebar() && (
                        <div id="layoutSidenav_nav">
                            <SideBar />
                        </div>
                    )}

                    <div id="layoutSidenav_content">
                        <Routes>
                            <Route
                                path="/"
                                element={isLoggedIn ? <MainBar /> : <Navigate to="/login" />}
                            />
                            <Route path="/dashboard" element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />} />
                            <Route path="/barchart" element={isLoggedIn ? <BarChart /> : <Navigate to="/login" />} />
                            <Route path="/mychart" element={isLoggedIn ? <MyChart /> : <Navigate to="/login" />} />
                            <Route path="/task" element={isLoggedIn ? <Task /> : <Navigate to="/login" />} />
                            <Route path="/class" element={isLoggedIn ? <Dclass /> : <Navigate to="/login" />} />
                            <Route path="/syllubus" element={isLoggedIn ? <Syllubus /> : <Navigate to="/login" />} />
                            <Route
                                path="/login"
                                element={<Login handleLogin={this.handleLogin} />}
                            />
                            <Route
                                path="/register"
                                element={<Register handleRegistration={this.handleRegistration} />}
                            />
                            <Route path="/forgetps" element={<ForgetPs />} />
                        </Routes>
                    </div>
                </div>
            </Router>
        );
    }
}

export default App;
