import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./component/NavBar";
import SideBar from "./component/Side_bar/SideBar";
import MainBar from "./component/Dashboard/MainBar";
import Login from "./component/Login";
import Register from "./component/Register";
import ForgetPs from "./component/ForgetPs";
import MyChart from "./component/Linechart/MyChart";
import BarChart from "./component/Task_status/Task_status_api";
import Dashboard from "./component/Dashboard/Dashboard";
import Task from "./component/Tasks/Task_api";
import Dclass from "./component/Side_bar/Dclass";
import Syllubus from "./component/Side_bar/Syllubus";

const App = () => {
    // State to manage the sidebar toggle
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    // Function to toggle the sidebar
    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    // Effect to add class to the body when component mounts
    useEffect(() => {
        document.body.classList.toggle('sb-nav-fixed', true);
    }, []);

    // Effect to toggle class based on isSidebarOpen state change
    useEffect(() => {
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    const path = window.location.pathname;

    return (
        <Router>
            <div id="layoutSidenav">
                {/* Render the NavBar based on the current path */}
                {path !== '/login' && path !== '/register' && path !== '/forgetps' && (
                    <nav>
                        <NavBar toggleSidebar={toggleSidebar} />
                    </nav>
                )}
                {/* Render the Sidebar based on the current path */}
                {path !== '/login' && path !== '/register' && path !== '/forgetps' && (
                    <div id="layoutSidenav_nav">
                        <SideBar />
                    </div>
                )}

                <div id="layoutSidenav_content">
                    {/* Define routes for different components */}
                    <Routes>
                        <Route path="/" element={<MainBar />} />
                        <Route path="/dashboard" element={<Dashboard />} />
                        <Route path="/barchart" element={<BarChart />} />
                        <Route path="/mychart" element={<MyChart />} />
                        <Route path="/task" element={<Task />} />
                        <Route path="/class" element={<Dclass />} />
                        <Route path="/syllubus" element={<Syllubus />} />
                        <Route path="/login" element={<Login />} />
                        <Route path="/register" element={<Register />} />
                        <Route path="/forgetps" element={<ForgetPs />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;







