import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        // Always add 'sb-nav-fixed' class to the body and conditionally add/remove 'sb-sidenav-toggled' class
        document.body.classList.toggle('sb-nav-fixed', true);
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    return (
        <Router>
            <nav>
                {isSidebarOpen && <NavBar toggleSidebar={toggleSidebar} />}
            </nav>
            <div id="layoutSidenav">
                <SideBar />
                <Routes>
                    <Route path="/" element={<MainBar />} />
                    <Route
                        path="/auth"
                        element={
                            isSidebarOpen ? (
                                <Navigate to="/auth" replace />
                            ) : (
                                <Auth />
                            )
                        }
                    />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
