import React, { useState } from 'react';

function DTask() {
  const [inputValues, setInputValues] = useState({
    sNo: '',
    batch: '',
    task: '',
    submissionDate: new Date().toISOString().slice(0, 10),
  });

  const [tasks, setTasks] = useState([]);
  const [editingTask, setEditingTask] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setInputValues({
      ...inputValues,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Create a new task object and add it to the tasks array
    if (editingTask) {
      const updatedTasks = tasks.map((task) =>
        task.id === editingTask.id ? inputValues : task
      );
      setTasks(updatedTasks);
      setEditingTask(null);
    } else {
      const newTask = { ...inputValues, id: Date.now() };
      setTasks([...tasks, newTask]);
    }

    // Clear the input fields after submission
    setInputValues({
      sNo: '',
      batch: '',
      task: '',
      submissionDate: new Date().toISOString().slice(0, 10),
    });
  };

  const handleEdit = (task) => {
    setInputValues(task);
    setEditingTask(task);
  };

  const handleDelete = (task) => {
    const updatedTasks = tasks.filter((t) => t.id !== task.id);
    setTasks(updatedTasks);
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
        <div className="mb-3">
            {/* ... (rest of the form fields) */}
        </div>
    </form>
    <div>&nbsp;</div>
    <h2>Task List</h2>
    <div className="card">
        <div className="card-body">
            <div className="table-responsive">
                <table className="table table-bordered" id="datatablesSimple">
                    <thead>
                        <tr>
                            <th>S.no</th>
                            <th>Batch</th>
                            <th>Task</th>
                            <th>Submission Date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tasks.map((task) => (
                            <tr key={task.id}>
                                {/* ... (table rows) */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</div>
  );
}

export default DTask;
