// Task component
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Task({ userId }) {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/users/${userId}/stasks`);
        if (response.status === 200) {
          setTasks(response.data.stasks);
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    if (userId) {
      fetchTasks();
    }
  }, [userId]);

  const handleDelete = async (taskId) => {
    try {
      const response = await axios.delete(`http://localhost:3001/deleteTask/${taskId}`);
      if (response.status === 200) {
        const updatedTasks = tasks.filter(task => task._id !== taskId);
        setTasks(updatedTasks);
      } else {
        console.error('Task deletion failed');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <table>
        {/* Display tasks */}
        <tbody>
          {tasks.map((task, index) => (
            <tr key={task._id}>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <button onClick={() => handleDelete(task._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Task;
