import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Register = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:3001/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name, email, password }),
            });

            if (response.ok) {
                const result = await response.json();
                console.log(result.message); // Log the success message
                // Redirect to the login page after successful registration
                window.location.href = '/login'; // Replace with your routing logic
            } else {
                const errorData = await response.json();
                console.error(errorData.error); // Log the error message
                // Handle registration failure
            }
        } catch (error) {
            console.error('Error:', error);
            // Handle other unexpected errors
        }
    };

    return (
        <div className="bg-white">
            {/* Your HTML structure for the registration form */}
            <div id="layoutAuthentication">
                {/* Other layout elements */}
            </div>

            <form onSubmit={handleSubmit}>
                {/* Input fields for registration details */}
                <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="Enter your name"
                />
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter your email"
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Create a password"
                />
                <input
                    type="password"
                    placeholder="Confirm password"
                    // Add onChange and value properties for confirmation, if needed
                />
                <button type="submit">Register</button>
            </form>

            <div className="card-footer text-center py-3">
                <div className="small">
                    <Link to="/login">Have an account? Go to login</Link>
                </div>
            </div>
        </div>
    );
};

export default Register;
