import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function Dbarchart() {
  const barChartRef = useRef(null);
  const donutChartRef = useRef(null);

  useEffect(() => {
    // Get the canvas elements for both the bar and donut charts
    var barCtx = document.getElementById('myBarChart').getContext('2d');
    var donutCtx = document.getElementById('myDonutChart').getContext('2d');

    // Destroy existing charts if they exist
    if (barChartRef.current) {
      barChartRef.current.destroy();
    }
    if (donutChartRef.current) {
      donutChartRef.current.destroy();
    }

    // Define data for the bar chart
    var barData = {
      labels: ['January', 'February', 'March', 'April', 'May'],
      datasets: [
        {
          label: 'Bar Chart Data',
          data: [12, 19, 3, 5, 2],
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    // Create the bar chart
    barChartRef.current = new Chart(barCtx, {
      type: 'bar',
      data: barData,
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });

    // Define data for the donut chart
    var donutData = {
      labels: ['Red', 'Green', 'Blue'],
      datasets: [
        {
          data: [30, 40, 30],
          backgroundColor: ['red', 'green', 'blue'],
        },
      ],
    };

    // Create the donut chart
    donutChartRef.current = new Chart(donutCtx, {
      type: 'doughnut',
      data: donutData,
    });
  }, []);

  return (
    <div id="layoutSidenav_content">
      <div className="col-lg-6">
        <div className="card mb-4">
          <div className="card-header">
            <i className="fas fa-chart-bar me-1"></i>
            Bar Chart Example
          </div>
          <div className="card-body">
            <canvas id="myBarChart" width="100%" height="50"></canvas>
          </div>
          <div className="card-footer small text-muted">Updated yesterday at 11:59 PM</div>
        </div>
      </div>
      <div className="col-lg-6">
        <div className="card mb-4">
          <div className="card-header">
            <i className="fas fa-chart-pie me-1"></i>
            Donut Chart Example
          </div>
          <div className="card-body">
            <canvas id="myDonutChart" width="100%" height="50"></canvas>
          </div>
          <div className="card-footer small text-muted">Updated yesterday at 11:59 PM</div>
        </div>
      </div>
    </div>
  );
}

export default Dbarchart;
