import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const TasksSubmission = ({ userId }) => {
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    link: '',
    status: '',
    completedOn: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3001/users/${userId}/stasks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        console.log('Task submitted successfully');
        setNewTask({
          taskName: '',
          description: '',
          link: '',
          status: '',
          completedOn: '',
        });
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const setNewTaskField = (field, value) => {
    setNewTask({ ...newTask, [field]: value });
  };

  return (
    <div>
      <h3>Task Submission</h3>
      <Form onSubmit={handleSubmit} className="mt-4">
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newTask.taskName}
              onChange={(e) => setNewTaskField('taskName', e.target.value)}
              placeholder="New Task Name"
            />
          </Col>
        </Row>
        {/* Other form fields for description, status, link, and completion date */}
        <Button type="submit">Submit</Button>
      </Form>
    </div>
  );
};

export default TasksSubmission;
