import React, { useState, useEffect } from 'react';

function Task() {
  const [tasks, setTasks] = useState([]);
  const [taskName, setTaskName] = useState('');
  const [description, setDescription] = useState('');
  const [link, setLink] = useState('');
  const [status, setStatus] = useState('');
  const [completedOn, setCompletedOn] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:3001/users/1/stasks');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setTasks(data.stasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    const newTask = { taskName, description, link, status, completedOn };
    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        fetchTasks(); // Reload tasks after creating a new one
        setTaskName('');
        setDescription('');
        setLink('');
        setStatus('');
        setCompletedOn('');
      } else {
        console.error('Task creation failed!');
      }
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Tasks</h1>
      <form onSubmit={handleCreate}>
        <div className="mb-3">
          <label htmlFor="taskName" className="form-label">Task Name:</label>
          <input
            type="text"
            className="form-control"
            id="taskName"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description:</label>
          <textarea
            className="form-control"
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="link" className="form-label">Link:</label>
          <input
            type="text"
            className="form-control"
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="status" className="form-label">Status:</label>
          <input
            type="text"
            className="form-control"
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="completedOn" className="form-label">Completed On:</label>
          <input
            type="text"
            className="form-control"
            id="completedOn"
            value={completedOn}
            onChange={(e) => setCompletedOn(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit Task</button>
      </form>
      <ul className="list-group mt-4">
        {tasks.map((task, index) => (
          <li key={index} className="list-group-item">
            <h5>Task {index + 1}</h5>
            <p>Task Name: {task.taskName}</p>
            <p>Description: {task.description}</p>
            <p>Link: {task.link}</p>
            <p>Status: {task.status}</p>
            <p>Completed On: {task.completedOn}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Task;
