import { BrowserRouter as Router, Route, Routes, Outlet,useState,useEffect } from "react-router-dom";
import NavBar from "./component/NavBar";
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Login from "./component/Login";
import Register from "./component/Register";
import ForgetPs from "./component/ForgetPs";
import MyChart from "./component/MyChart";
import BarChart from "./component/BarChart";
import Dashboard from "./component/Dashboard";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        document.body.classList.toggle('sb-nav-fixed', true);
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    return (
        <Router>
            <div id="layoutSidenav" className={isSidebarOpen ? 'sb-sidenav-toggled' : ''}>
                <nav>
                    <NavBar toggleSidebar={toggleSidebar} />
                </nav>
                <Routes>
                    <Route path="/" element={<SideBar />} />
                    <Route path="/" element={<MainBar />}>
                        {/* Use Outlet to render nested routes */}
                        <Outlet />
                    </Route>
                    <Route path="/dashboard" element={<Dashboard />} />
                    <Route path="/mychart" element={<MyChart />} />
                    <Route path="/barchart" element={<BarChart />} />
                </Routes>
            </div>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/forgetps" element={<ForgetPs />} />
            </Routes>
        </Router>
    );
}

export default App;
