import { useState, useEffect } from 'react';

const useContent = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    const auth = localStorage.getItem('isAuthenticated');
    return auth ? JSON.parse(auth) : false;
  });

  const [tasks, setTasks] = useState([]);

  const handleLogin = async (email, password) => {
    try {
      const response = await fetch("http://localhost:3001/signin", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const result = await response.json();

        if (result.message === 'Sign-in successful') {
          setIsAuthenticated(true);
          localStorage.setItem('isAuthenticated', true);

          const userResponse = await fetch(`http://localhost:3001/tasks/${result.user._id}`);

          if (userResponse.ok) {
            const userTasks = await userResponse.json();
            setTasks(userTasks); // Set the user's tasks to state
          } else {
            console.error('Error fetching user tasks');
          }
        } else {
          console.error("Unexpected response:", result.message);
        }
      } else {
        console.error("HTTP error:", response.status);
        setIsAuthenticated(false);
        localStorage.removeItem('isAuthenticated');
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('isAuthenticated');
  };

  useEffect(() => {
    const storedIsAuthenticated = localStorage.getItem('isAuthenticated');
    if (storedIsAuthenticated && JSON.parse(storedIsAuthenticated) === true) {
      setIsAuthenticated(true);
      // If authenticated, fetch the user's tasks
      // ... Fetch tasks as needed and update 'tasks' state
    }
  }, []);

  return { isAuthenticated, tasks, handleLogin, handleLogout };
};

export default useContent;
