import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function Dchart() {
  const chartRef = React.useRef(null);
  const chartInstanceRef = React.useRef(null); // To store the Chart instance

  React.useEffect(() => {
    const ctx = chartRef.current.getContext('2d');
    const labels = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']; // Replace with your actual labels
    const data = {
      labels: labels,
      datasets: [{
        label: 'Year 2023',
        data: [65, 59, 80, 81, 56, 55, 40],
        fill: false,
        borderColor: 'rgb(106, 90, 205)',
        tension: 0.1
      }]
    };
    const config = {
      type: 'line',
      data: data,
      options: {
        responsive: true,
        maintainAspectRatio: false,
      },
    };

    // Destroy the previous Chart instance
    if (chartInstanceRef.current) {
      chartInstanceRef.current.destroy();
    }

    // Create the new Chart instance
    chartInstanceRef.current = new Chart(ctx, config);
  }, []);

  return (
    <div className="card mb-4">
      <div className="card-header">
        <i className="fas fa-chart-area me-1"></i>
        Montly Progress Chart
      </div>
      <div className="card-body">
        <canvas ref={chartRef} id="dailyActivity" ></canvas>
      </div>
      <div className="card-footer small text-muted">Updated yesterday at 11:59 PM</div>
    </div>
  );
}

export default Dchart;
