import React, { useState, useEffect } from 'react';

function Task() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    // Function to fetch tasks
    const fetchTasks = async () => {
      const response = await fetch('http://localhost:3001/users/1/stasks'); // Replace '1' with the batch or user ID
      const data = await response.json();
      setTasks(data.stasks);
    };

    fetchTasks();
  }, []);

  // Function to delete a task
  const deleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3001/tasks/${taskId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // If the deletion was successful, update the task list by fetching the updated list
        fetchTasks();
      } else {
        console.error('Task deletion failed!');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h1>Tasks</h1>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            <p>Task Name: {task.taskName}</p>
            <p>Description: {task.description}</p>
            <p>Status: {task.status}</p>
            <p>Completed On: {task.completedOn}</p>
            <button onClick={() => deleteTask(task.id)}>Delete</button>
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Task;
