import React, { useState, useEffect } from 'react';

const TaskSubmission = () => {
  const [userId, setUserId] = useState(1); // Assuming a default user ID
  const [tasks, setTasks] = useState([]);
  const [taskName, setTaskName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('Pending');
  const [completedOn, setCompletedOn] = useState('');
  const [link, setLink] = useState('');

 
  const fetchTasks = async () => {
    try {
      const response = await fetch(`http://localhost:3001/tasks/${userId}`);

      if (response.ok) {
        const data = await response.json();
        setTasks(data.stasks);
      } else {
        console.error('Failed to fetch tasks');
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };
  useEffect(() => {
    fetchTasks();
  }, []);


  const createTask = async () => {
    try {
      const response = await fetch('http://localhost:3001/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          taskName,
          description,
          status,
          completedOn,
          link,
        }),
      });

      if (response.ok) {
        fetchTasks(); // Refresh tasks after adding a new one
        setTaskName('');
        setDescription('');
        setStatus('Pending');
        setCompletedOn('');
        setLink('');
      } else {
        console.error('Task creation failed');
      }
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  const deleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3001/tasks/${userId}/${taskId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        fetchTasks(); // Refresh tasks after deletion
      } else {
        console.error('Task deletion failed');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createTask();
  };

  return (
    <div className="container mt-5">
      <h2>Task CRUD</h2>
      <form onSubmit={handleSubmit}>
        {/* Create */}
        <div className="mb-3">
          <label htmlFor="taskName">Task Name:</label>
          <input
            type="text"
            id="taskName"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="status">Status:</label>
          <input
            type="text"
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="link">Link:</label>
          <input
            type="text"
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="completedOn">Completed On:</label>
          <input
            type="text"
            id="completedOn"
            value={completedOn}
            onChange={(e) => setCompletedOn(e.target.value)}
          />
        </div>
        <button type="submit">Create Task</button>
      </form>

      {/* Read and Delete */}
      <div>
        <h3>Tasks:</h3>
        <ul>
          {tasks.map((task) => (
            <li key={task.day}>
              <p>Task: {task.taskName}</p>
              <p>Description: {task.description}</p>
              <p>Status: {task.status}</p>
              <p>Completed On: {task.completedOn}</p>
              <button onClick={() => deleteTask(task.day)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TaskSubmission;
