import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import DoughnutChart from '../Dashboard/DoughnutChart';
import axios from 'axios';

function Task() {
  const [tasks, setTasks] = useState([]);
  const [userId, setUserId] = useState(''); // State to store the user ID after login

  useEffect(() => {
    // Fetch tasks if a user ID is available
    if (userId) {
      const fetchTasks = async () => {
        try {
          const response = await axios.get(`http://localhost:3001/users/${userId}/stasks`);
          if (response.status === 200) {
            setTasks(response.data.stasks);
          } else {
            console.error('Failed to fetch tasks');
          }
        } catch (error) {
          console.error('Error fetching tasks:', error);
        }
      };

      fetchTasks();
    }
  }, [userId]);

  const handleDelete = async (taskId) => {
    try {
      const response = await axios.delete(`http://localhost:3001/deleteTask/${taskId}`);
      if (response.status === 200) {
        const updatedTasks = tasks.filter(task => task._id !== taskId);
        setTasks(updatedTasks);
      } else {
        console.error('Task deletion failed');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          {/* Table headers */}
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={task._id}>
              {/* Table rows */}
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
