import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
// YourComponent code to display userId
const YourComponent = ({ userId }) => {
  return (
    <div>
      <p>User ID: {userId}</p>
      {/* Other component content */}
    </div>
  );
};


const TasksSubmission = ({ userId }) => {
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    link: '',
    status: '',
    completedOn: '',
  });

  const { taskName, description, link, status, completedOn } = newTask;

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3001/users/${userId}/stasks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        console.log('Task submitted successfully');
        setNewTask({
          taskName: '',
          description: '',
          link: '',
          status: '',
          completedOn: '',
        });
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const setNewTaskField = (field, value) => {
    setNewTask({ ...newTask, [field]: value });
  };

  return (
    <div>
      <h3>Task Submission</h3>
      <YourComponent userId={userId} />
      <Form onSubmit={handleSubmit} className="mt-4">
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={taskName}
              onChange={(e) => setNewTaskField('taskName', e.target.value)}
              placeholder="New Task Name"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={description}
              onChange={(e) => setNewTaskField('description', e.target.value)}
              placeholder="Description"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={status}
              onChange={(e) => setNewTaskField('status', e.target.value)}
              placeholder="Status"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={completedOn}
              onChange={(e) => setNewTaskField('completedOn', e.target.value)}
              placeholder="Completed On (Date)"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={link}
              onChange={(e) => setNewTaskField('link', e.target.value)}
              placeholder="Task Link"
            />
          </Col>
        </Row>
        <Button type="submit">Submit</Button>
      </Form>
    </div>
  );
};

export default TasksSubmission;
