import React, { useState } from 'react';
import axios from 'axios';
import useContent from '../../useContent';

const TaskSubmission = () => {
  const { isAuthenticated } = useContent();
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    status: '',
    completedOn: '',
    link: '',
  });
  const [error, setError] = useState('');

  const handleTaskSubmission = async (e) => {
    e.preventDefault();

    if (!isAuthenticated) {
        console.error('User not authenticated. Please log in.');
        return;
    }

    try {
        const userEmail = localStorage.getItem('userEmail');

        // Validating the required fields
        if (!newTask.taskName || !newTask.description || !newTask.status) {
            setError('Please fill in all required fields.');
            return;
        }

        console.log('Starting task submission...');
const response = await axios.post('http://localhost:3001/submitTask', {
    ...newTask,
    email: userEmail,
});

        if (response.status === 200) {
            console.log('Task submitted:', response.data.task);
            // Reset the form after successful submission
            setNewTask({
                taskName: '',
                description: '',
                status: '',
                completedOn: '',
                link: '',
            });
            setError(''); // Clear any previous error messages
        } else {
            console.error('Task submission failed');
        }
    } catch (error) {
        console.error('Error submitting task:', error);
        setError('Error submitting task. Please try again.');
    }
};

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  return (
    <div>
      <h2>Task Submission</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleTaskSubmission}>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="taskName"
            value={newTask.taskName}
            onChange={handleChange}
            placeholder="Task Name"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="description"
            value={newTask.description}
            onChange={handleChange}
            placeholder="Description"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="status"
            value={newTask.status}
            onChange={handleChange}
            placeholder="Status"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="completedOn"
            value={newTask.completedOn}
            onChange={handleChange}
            placeholder="Completed On"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="link"
            value={newTask.link}
            onChange={handleChange}
            placeholder="Task Link"
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default TaskSubmission;
