import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        // Always add 'sb-nav-fixed' class to the body and conditionally add/remove 'sb-sidenav-toggled' class
        document.body.classList.toggle('sb-nav-fixed'," ");
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    return (

        <Router>
             <div>
            <NavBar toggleSidebar={toggleSidebar} />
            </div>  
            <div id="layoutSidenav">

                <SideBar />
                <Routes>
                    <Route path="/" element={<MainBar />} />
                </Routes>
            </div>
            <div className="bg-primary">
            <div id="layoutAuthentication">
            <Routes>
                <Route path="/auth" element={<Auth />} />
            </Routes>
            </div>
            </div>
            <div id="layoutAuthentication_footer">
            <footer className="py-4 bg-light mt-auto">
                <div className="container-fluid px-4">
                    <div className="d-flex align-items-center justify-content-between small">
                        <div className="text-muted">Copyright &copy; Your Website 2023</div>
                        <div>
                            <a href="#!">Privacy Policy</a>
                            &middot;
                            <a href="#!">Terms &amp; Conditions</a>
                        </div>
                    </div>
                </div>
            </footer>
        </div>

        </Router>
    );
}

export default App;
