import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const location = useLocation();

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    useEffect(() => {
        // Always add 'sb-nav-fixed' class to the body and conditionally add/remove 'sb-sidenav-toggled' class
        document.body.classList.toggle('sb-nav-fixed', true);
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);

    // Determine whether to show the NavBar based on the current route path
    const shouldShowNavBar = location.pathname !== "/auth";

    return (
        <Router>
            <nav>
                {shouldShowNavBar && <NavBar toggleSidebar={toggleSidebar} />}
            </nav>
            <div id="layoutSidenav">
                <SideBar />
                <Routes>
                    <Route path="/" element={<MainBar />} />
                </Routes>
            </div>
            <div className="bg-primary">
                <div id="layoutAuthentication">
                    <Routes>
                        <Route path="/auth" element={<Auth />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
