import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const TaskSubmission = () => {
  const [tasks, setTasks] = useState([]);
  const [newTaskName, setNewTaskName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newTaskStatus, setNewTaskStatus] = useState('');
  const [newCompletedOn, setNewCompletedOn] = useState('');
  const [newLink, setNewLink] = useState('');

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:3001/users/:email/stasks');

        if (response.ok) {
          const data = await response.json();
          setTasks(data.stasks);
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          taskName: newTaskName,
          description: newDescription,
          link: newLink,
          status: newTaskStatus,
          completedOn: newCompletedOn,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setTasks([...tasks, data.newTask]);
        setNewTaskName('');
        setNewDescription('');
        setNewTaskStatus('');
        setNewCompletedOn('');
        setNewLink('');
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  return (
    <Container>
      <h2 className="mt-4">Task Submission</h2>
      <Form onSubmit={handleSubmit} className="mt-4">
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newTaskName}
              onChange={(e) => setNewTaskName(e.target.value)}
              placeholder="New Task Name"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newDescription}
              onChange={(e) => setNewDescription(e.target.value)}
              placeholder="Description"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newTaskStatus}
              onChange={(e) => setNewTaskStatus(e.target.value)}
              placeholder="Status"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newCompletedOn}
              onChange={(e) => setNewCompletedOn(e.target.value)}
              placeholder="Completed On (Date)"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newLink}
              onChange={(e) => setNewLink(e.target.value)}
              placeholder="Task Link"
            />
          </Col>
        </Row>
        <Button type="submit">Submit</Button>
      </Form>
    </Container>
  );
};

export default TaskSubmission;
