import { useState, useEffect } from 'react';

const useContent = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(() => {
        // Get the authentication status from localStorage if available
        const auth = localStorage.getItem('isAuthenticated');
        return auth ? JSON.parse(auth) : false;
    });

    const handleLogin = async (email, password) => {
        try {
            console.log('Logging in with email:', email); // Log the email before sending it to the server
            const response = await fetch("http://localhost:3001/signin", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            console.log('Response status:', response.status); // Log the response status
            // Add other response logs as needed (e.g., response body)

            if (response.ok) {
                const result = await response.json();
                if (result === 'success') {
                    setIsAuthenticated(true);
                    localStorage.setItem('isAuthenticated', true);
                } else {
                    // Handle unexpected response (successful status but not 'success')
                    console.error("Unexpected response:", result);
                    // Perform an action or display an error to the user
                }
            } else {
                // Handle non-200 HTTP response status
                console.error("HTTP error:", response.status);
                setIsAuthenticated(false);
                localStorage.removeItem('isAuthenticated');
                // Display an error message to the user
            }
        } catch (error) {
            console.error("Error:", error);
            // Handle any other unexpected error during the fetch
            // Display an error message to the user
        }
    };
     
    const handleLogout = () => {
        setIsAuthenticated(false);
        localStorage.removeItem('isAuthenticated'); // Remove the authentication status on logout
    };

    useEffect(() => {
        const storedIsAuthenticated = localStorage.getItem('isAuthenticated');
        if (storedIsAuthenticated && JSON.parse(storedIsAuthenticated) === true) {
            setIsAuthenticated(true);
        }
    }, []);

    return { isAuthenticated, handleLogin, handleLogout };
};

export default useContent;
