import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

function Task() {
  const [tasks, setTasks] = useState([]);
  const [newTaskName, setNewTaskName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Function to fetch tasks
    const fetchTasks = async () => {
      const response = await fetch('http://localhost:3001/users/1/stasks'); // Replace '1' with the batch or user ID
      const data = await response.json();
      setTasks(data.stasks);
    };

    fetchTasks();
  }, []);

  const handleDelete = async (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);

    // Here you can also send a DELETE request to your backend API to delete the task
    try {
      await fetch(`http://localhost:3001/tasks/${tasks[index].id}`, {
        method: 'DELETE',
      });
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddTask = async () => {
    // Assuming the task is added to the same endpoint for simplicity
    try {
      const response = await fetch('http://localhost:3001/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          taskName: newTaskName,
          description: newDescription,
          // Add other task properties if needed
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setTasks([...tasks, data.newTask]);
        setNewTaskName('');
        setNewDescription('');
      } else {
        console.error('Task creation failed');
      }
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div>
      <h1>Tasks</h1>
      <form onSubmit={(e) => { e.preventDefault(); handleAddTask(); }}>
        <input
          type="text"
          value={newTaskName}
          onChange={(e) => setNewTaskName(e.target.value)}
          placeholder="New Task Name"
        />
        <input
          type="text"
          value={newDescription}
          onChange={(e) => setNewDescription(e.target.value)}
          placeholder="Description"
        />
        <Button type="submit">Add Task</Button>
      </form>
      <Table striped bordered hover>
        {/* ... existing table code to display tasks */}
      </Table>
    </div>
  );
}

export default Task;
