import React, { useEffect, createContext, useReducer, useContext } from 'react';

const AuthContext = createContext();

const initialState = {
  isAuthenticated: false,
  user: null // Add user object to the initial state
};

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN':
      return { ...state, isAuthenticated: true, user: action.payload }; // Set user object on login
    case 'LOGOUT':
      return { ...state, isAuthenticated: false, user: null }; // Clear user object on logout
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  useEffect(() => {
    // Perform necessary operations when isAuthenticated or user changes
    // e.g., save user details to localStorage
    // localStorage.setItem('user', state.user);
  }, [state.isAuthenticated, state.user]);

  return (
    <AuthContext.Provider value={{ state, dispatch }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};

export default AuthContext;
