import React, { useState } from 'react';

const TaskSubmission = () => {
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    status: '',
    completedOn: '',
    link: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Perform the submission logic
      // Use fetch or any library to send data to the backend
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        const data = await response.json();
        // Handle successful task submission
        console.log('Task submitted:', data);
        // You might want to update the tasks list after submission
        // Fetch tasks again, update state, etc.
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  return (
    <div>
      <h2>Task Submission</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="taskName"
          value={newTask.taskName}
          onChange={handleChange}
          placeholder="Task Name"
        />
        <input
          type="text"
          name="description"
          value={newTask.description}
          onChange={handleChange}
          placeholder="Description"
        />
        <input
          type="text"
          name="status"
          value={newTask.status}
          onChange={handleChange}
          placeholder="Status"
        />
        <input
          type="text"
          name="completedOn"
          value={newTask.completedOn}
          onChange={handleChange}
          placeholder="Completed On"
        />
        <input
          type="text"
          name="link"
          value={newTask.link}
          onChange={handleChange}
          placeholder="Task Link"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default TaskSubmission;
