import React, { useState} from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(" ");

    // const toggleSidebar = () => {
    //     setIsSidebarOpen(!isSidebarOpen);
    // };

    // useEffect(() => {
    //     // Always add 'sb-nav-fixed' class to the body and conditionally add/remove 'sb-sidenav-toggled' class
    //     document.body.classList.toggle('sb-nav-fixed'," ");
    //     document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    // }, [isSidebarOpen]);


    const toggleSidebar= document.body.querySelector(isSidebarOpen);
    if (setIsSidebarOpen) {
        // Uncomment Below to persist sidebar toggle between refreshes
        // if (localStorage.getItem('sb|sidebar-toggle') === 'true') {
        //     document.body.classList.toggle('sb-sidenav-toggled');
        // }
        setIsSidebarOpen.addEventListener('click', event => {
            event.preventDefault();
            document.body.classList.toggle('sb-sidenav-toggled');
            localStorage.setItem('sb|sidebar-toggle', document.body.classList.contains('sb-sidenav-toggled'));
        });
    }

    return (
        <Router>
               <nav>
                 <NavBar toggleSidebar={toggleSidebar} />
             </nav>
            <div id="layoutSidenav">

                <SideBar />
                <Routes>
                    <Route path="/" element={<MainBar />} />
                </Routes>
            </div>
            <div className="bg-primary">
            <div id="layoutAuthentication">
            <Routes>
                <Route path="/auth" element={<Auth />} />
            </Routes>
            </div>
            </div>


        </Router>
    );
}

export default App;
