import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function DoughnutChart({ tasks }) {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    if (tasks && tasks.length > 0) {
      const completedTasks = tasks.filter(task => task.status === 'completed').length;
      const submittedTasks = tasks.filter(task => task.status === 'SUBMITTED TASK').length;
      const balanceTasks = tasks.filter(task => task.status !== 'completed' && task.status !== 'SUBMITTED TASK').length;
      const totalTasks = tasks.length;

      const ctx = chartRef.current.getContext('2d');
      const data = {
        labels: ['Task Completed', 'Submitted Task','Last Submitted Task', 'Total Task'],
        datasets: [
          {
            label: 'Task',
            data: [completedTasks, submittedTasks,balanceTasks, totalTasks],
            backgroundColor: ['#772F67', '#FFCA3E','#D082A2', '#619ED6'],
            hoverOffset: 4,
          },
        ],
      };
      const config = {
        type: 'doughnut',
        data: data,
        options: {
          responsive: true,
          maintainAspectRatio: false,
        },
      };

      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }

      chartInstanceRef.current = new Chart(ctx, config);

      return () => {
        if (chartInstanceRef.current) {
          chartInstanceRef.current.destroy();
        }
      };
    }
  }, [tasks]);

  return (
    <div className="card mb-4">
      <div className="card-header">
        <i className="fas fa-chart-pie me-1"></i>
        Task Status Chart
      </div>
      <div className="card-body" style={{ height: '400px' }}>
        <canvas ref={chartRef} id="doughnutChart"></canvas>
      </div>
      <div className="card-footer small text-muted">Updated yesterday at 11:59 PM</div>
    </div>
  );
}

export default DoughnutChart;
