import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

const TaskSubmission = () => {
  const [tasks, setTasks] = useState([]);
  const [newTaskName, setNewTaskName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newTaskStatus, setNewTaskStatus] = useState('');
  const [newCompletedOn, setNewCompletedOn] = useState('');
  const [newLink, setNewLink] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:3001/users/1/stasks'); // Fetch tasks for a specific user ID (e.g., user ID 1)

        if (response.ok) {
          const data = await response.json();
          setTasks(data.stasks);
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          // day: `d${tasks.length + 1}`, // Generate a new day based on the current number of tasks
          taskName: newTaskName,
          description: newDescription,
          link: newLink,
          status: newTaskStatus,
          completedOn: newCompletedOn,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setTasks([...tasks, data.newTask]);
        setNewTaskName('');
        setNewDescription('');
        setNewTaskStatus('');
        setNewCompletedOn('');
        setNewLink('');
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const handleDelete = async (index) => {
    try {
      const response = await fetch(`http://localhost:3001/tasks/1/${tasks[index].day}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        const updatedTaskList = [...tasks];
        updatedTaskList.splice(index, 1);
        setTasks(updatedTaskList);
      } else {
        console.error('Task deletion failed');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h2>Task Management</h2>
      <form onSubmit={handleSubmit}>
      <input
          type="text"
          value={newTaskName}
          onChange={(e) => setNewTaskName(e.target.value)}
          placeholder="New Task Name"
        />
        <input
          type="text"
          value={newDescription}
          onChange={(e) => setNewDescription(e.target.value)}
          placeholder="Description"
        />
        <input
          type="text"
          value={newTaskStatus}
          onChange={(e) => setNewTaskStatus(e.target.value)}
          placeholder="Task Status"
        />
        <input
          type="text"
          value={newCompletedOn}
          onChange={(e) => setNewCompletedOn(e.target.value)}
          placeholder="Completed On (Date)"
        />
        <input
          type="text"
          value={newLink}
          onChange={(e) => setNewLink(e.target.value)}
          placeholder="Task Link"
        />
        <Button type="submit">Add Task</Button>
      </form>

   
    </div>
  );
};

export default TaskSubmission;
