import React, { useState } from 'react';

const TaskSubmission = () => {
  const [taskName, setTaskName] = useState('');
  const [description, setDescription] = useState('');

  // Assuming the initial status is 'Pending'
  const initialStatus = 'Pending';
  const initialCompletedOn = null;

  const handleSubmit = async (e) => {
    e.preventDefault();

    const taskData = {
      taskName,
      description,
      status: initialStatus, // Include the initial status in task submission
      completedOn: initialCompletedOn, // Include the initial completion date in task submission
      // Additional fields, if any
    };

    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        // Task submitted successfully
        console.log('Task submitted!');
        // Reset form fields after submission
        setTaskName('');
        setDescription('');
      } else {
        // Handle the case where the submission failed
        console.error('Task submission failed!');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  return (
    <div>
      <h2>Submit a New Task</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Task Name:</label>
          <input
            type="text"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <button type="submit">Submit Task</button>
      </form>
    </div>
  );
};

export default TaskSubmission;
