import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';

const TasksSubmission = ({ userId }) => {
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    link: '',
    status: '',
    completedOn: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:3001/users/${userId}/stasks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        // You might update the state to display the newly added task
        console.log('Task submitted successfully');
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  return (
    <div>
      <h3>Task Submission</h3>
      <Form onSubmit={handleSubmit} className="mt-4">
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newTaskName}
              onChange={(e) => setNewTaskName(e.target.value)}
              placeholder="New Task Name"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newDescription}
              onChange={(e) => setNewDescription(e.target.value)}
              placeholder="Description"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newTaskStatus}
              onChange={(e) => setNewTaskStatus(e.target.value)}
              placeholder="Status"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newCompletedOn}
              onChange={(e) => setNewCompletedOn(e.target.value)}
              placeholder="Completed On (Date)"
            />
          </Col>
        </Row>
        <Row className="mb-3">
          <Col>
            <Form.Control
              type="text"
              value={newLink}
              onChange={(e) => setNewLink(e.target.value)}
              placeholder="Task Link"
            />
          </Col>
        </Row>
        <Button type="submit">Submit</Button>
      </Form>
    </div>
  );
};

export default TasksSubmission;
