import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import DoughnutChart from '../Dashboard/DoughnutChart';
const userId = '6549d7bcaa68df7803580d3d'; // Replace this with the actual userId
fetch(`http://localhost:3001/users/${userId}/stasks`)
  .then((response) => {
    if (response.ok) {
      return response.json();
    }
    throw new Error('Failed to fetch tasks');
  })
  .then((data) => {
    // Handle the data (array of tasks) received from the backend
    console.log(data.stasks); // This array contains the tasks for the specified user
  })
  .catch((error) => {
    console.error('Error:', error);
  });

function Task({ userId }) {
  

  const [tasks, setTasks] = useState([]);
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`http://localhost:3001/users/${userId}/stasks`);
        if (response.ok) {
          const data = await response.json();
          setTasks(data.stasks);
        } else {
          console.error('Failed to fetch tasks');
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [userId]);

  const handleDelete = async (index) => {
    try {
      await fetch(`http://localhost:3001/users/${userId}/stasks/${tasks[index]._id}`, {
        method: 'DELETE',
      });
      const updatedTasks = tasks.filter((_, i) => i !== index);
      setTasks(updatedTasks);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <YourComponent userId={userId} />
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(index)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
