import React, { useEffect } from 'react';
import Chart from 'chart.js/auto'; // Import Chart.js

function Dchart() {
  useEffect(() => {
    // Get the chart canvas element
    var ctx = document.getElementById('myChart').getContext('2d'); // Change 'dchart' to 'myChart'

    // Define your data
    var data = {
      labels: ['January', 'February', 'March', 'April', 'May'],
      datasets: [
        {
          label: 'My Data',
          data: [12, 19, 3, 5, 2],
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    // Create the chart
    var Dchart = new Chart(ctx, {
      type: 'bar',
      data: data,
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  }, []); // Empty dependency array to run the effect only once

  return (
    <div>
      <canvas id="myChart" width="100" height="50"></canvas> {/* Change 'myChart' here */}
    </div>
  );
}

export default Dchart;
