import React, { useState } from 'react';

const TaskSubmission = () => {
  const [taskName, setTaskName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('');
  const [completedOn, setCompletedOn] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const taskData = {
      taskName,
      description,
      status,
      completedOn,
      // Additional fields, if any
    };

    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        // Task submitted successfully
        console.log('Task submitted!');
        // Reset form fields after submission
        setTaskName('');
        setDescription('');
        setStatus('Pending');
        setCompletedOn(null);
      } else {
        // Handle the case where the submission failed
        console.error('Task submission failed!');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Submit a New Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="taskName" className="form-label">Task Name:</label>
          <input
            type="text"
            className="form-control"
            id="taskName"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description:</label>
          <textarea
            className="form-control"
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="status" className="form-label">Status:</label>
          <input
            type="text"
            className="form-control"
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="completedOn" className="form-label">Completed On:</label>
          <input
            type="text"
            className="form-control"
            id="completedOn"
            value={completedOn}
            onChange={(e) => setCompletedOn(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit Task</button>
      </form>
    </div>
  );
};

export default TaskSubmission;
