import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../AuthContext'; // Adjust the path as needed
 // Update with the correct path

const TaskSubmission = () => {
  const { state } = useAuth();
  const { isAuthenticated, user } = state;
  
  const [newTask, setNewTask] = useState({
    taskName: '',
    description: '',
    status: '',
    completedOn: '',
    link: '',
  });

  const handleTaskSubmission = async (e) => {
    e.preventDefault();

    if (isAuthenticated && user) {
      try {
        const response = await axios.post('http://localhost:3001/submitTask', {
          ...newTask,
          email: user.email, // Use the authenticated user's email
        });

        if (response.status === 200) {
          console.log('Task submitted:', response.data.task);
          // Add the submitted task to the UI if needed
        } else {
          console.error('Task submission failed');
        }
      } catch (error) {
        console.error('Error submitting task:', error);
      }
    } else {
      console.error('User not authenticated');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  return (
    <div>
      <h2>Task Submission</h2>
      <form onSubmit={handleTaskSubmission}>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="taskName"
            value={newTask.taskName}
            onChange={handleChange}
            placeholder="Task Name"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="description"
            value={newTask.description}
            onChange={handleChange}
            placeholder="Description"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="status"
            value={newTask.status}
            onChange={handleChange}
            placeholder="Status"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="completedOn"
            value={newTask.completedOn}
            onChange={handleChange}
            placeholder="Completed On"
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            name="link"
            value={newTask.link}
            onChange={handleChange}
            placeholder="Task Link"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default TaskSubmission;
