import React, { useState, useEffect } from 'react';

const TaskSubmission = () => {
  const [taskName, setTaskName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('');
  const [completedOn, setCompletedOn] = useState('');
  const [link, setLink] = useState('');
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:3001/tasks');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setTasks(data.tasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const taskData = {
      taskName,
      description,
      status,
      completedOn,
      link,
    };

    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        console.log('Task submitted!');
        setTaskName('');
        setDescription('');
        setStatus('Pending');
        setCompletedOn(null);
        setLink('');
        fetchTasks();
      } else {
        console.error('Task submission failed!');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Submit a New Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="taskName" className="form-label">Task Name:</label>
          <input
            type="text"
            className="form-control"
            id="taskName"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description:</label>
          <textarea
            className="form-control"
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="status" className="form-label">Status:</label>
          <input
            type="text"
            className="form-control"
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="link" className="form-label">Link:</label>
          <input
            type="text"
            className="form-control"
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="completedOn" className="form-label">Completed On:</label>
          <input
            type="text"
            className="form-control"
            id="completedOn"
            value={completedOn}
            onChange={(e) => setCompletedOn(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit Task</button>
      </form>

      <h2>Tasks</h2>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            <div>
              <p>Task Name: {task.taskName}</p>
              <p>Description: {task.description}</p>
              <p>Link: {task.link}</p>
              <p>Status: {task.status}</p>
              <p>Completed On: {task.completedOn}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskSubmission;
