import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isLoginPage, setIsLoginPage] = useState(false);

    const toggleLoginPage = (value) => {
        setIsLoginPage(value);
    };

    return (
        <Router>
            <div id="app-container">
                {!isLoginPage && (
                    <div id="layoutSidenav" className="sb-sidenav-toggled">
                        <SideBar />
                    </div>
                )}
                <div id="layoutMain" className={isLoginPage ? 'sb-sidenav-toggled' : ''}>
                    {!isLoginPage && <NavBar toggleLoginPage={toggleLoginPage} />}
                    <Routes>
                        <Route path="/" element={<MainBar />} />
                    </Routes>
                </div>
            </div>
            <div className="bg-primary">
                <div id="layoutAuthentication">
                    <Routes>
                        <Route
                            path="/auth"
                            element={<Auth toggleLoginPage={toggleLoginPage} />}
                        />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
