// Import statements
import React, { useState } from 'react';
import TaskData from './Btask';
import Dbarchart from './Dbarchart';

function DTask() {
  const [inputValues, setInputValues] = useState({
    sNo: '',
    batch: '',
    task: '',
    submissionDate: new Date().toISOString().slice(0, 10),
    status: 'completed', // Set the default status to 'completed' for testing
  });

  const [tasks, setTasks] = useState(TaskData);

  const [editingTask, setEditingTask] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setInputValues({
      ...inputValues,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (editingTask) {
      const updatedTasks = tasks.map((task) =>
        task.sNo === editingTask.sNo ? inputValues : task
      );
      setTasks(updatedTasks);
      setEditingTask(null);
    } else {
      const newTask = { ...inputValues, submissionDate: inputValues.submissionDate.toString() };
      setTasks([...tasks, newTask]);
    }

    // Clear the input fields after submission
    setInputValues({
      sNo: '',
      batch: '',
      task: '',
      submissionDate: new Date().toISOString().slice(0, 10),
      status: 'completed', // Set the default status to 'completed' for the next submission
    });
  };

  const handleEdit = (task) => {
    setInputValues(task);
    setEditingTask(task);
  };

  const handleDelete = (task) => {
    const updatedTasks = tasks.filter((t) => t.sNo !== task.sNo);
    setTasks(updatedTasks);
  };

  // Function to update the chart data
  const updateChart = () => {
    // Update the chart data here
  };

  return (
    <div>
      <h2>Task Status Chart</h2>
      <Dbarchart taskData={tasks} updateChart={updateChart} />
      <form onSubmit={handleSubmit}>
        {/* Input fields */}
        <button type="submit" className="btn btn-primary">
          {editingTask ? 'Update Task' : 'Submit Task'}
        </button>
      </form>
      {/* Task List */}
      <div className="card">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-bordered" id="datatablesSimple">
              <thead>
                <tr>
                  <th>S.no</th>
                  <th>Batch</th>
                  <th>Task</th>
                  <th>Submission Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task) => (
                  <tr key={task.sNo}>
                    <td>{task.sNo}</td>
                    <td>{task.batch}</td>
                    <td>{task.task}</td>
                    <td>{task.submissionDate}</td>
                    <td>
                      <button onClick={() => handleEdit(task)} className="btn btn-sm btn-primary">
                        Edit
                      </button>
                      <button onClick={() => handleDelete(task)} className="btn btn-sm btn-danger">
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DTask;
