import React, { useState, useEffect } from 'react';

const Task = ({ userId }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch(`http://localhost:3001/tasks/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch tasks');
        }
        const data = await response.json();
        setTasks(data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [userId]);

  return (
    <div>
      <h3>Tasks</h3>
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task._id}>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Task;
