// App.js

import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import NavBar from './component/NavBar';
import Sidebar from './component/Sidebar/Sidebar';
import Login from './component/Login';
import Dashboard from './component/Dashboard/Dashboard';
import useContent from './useContent'; // Import the useContent hook

const App = () => {
  const { isAuthenticated } = useContent(); // Use the useContent hook

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={isAuthenticated ? <Navigate to="/dashboard" /> : <Login />}
        />
        <Route path="/login" element={<Login />} />
        <Route path="/dashboard" element={<DashboardWithLayout />} />
      </Routes>
    </Router>
  );
};

const DashboardWithLayout = () => {
  const { handleLogout } = useContent();

  return (
    <div id="wrapper">
      <NavBar handleLogout={handleLogout} />
      <div id="content-wrapper" className="d-flex flex-column">
        <div id="content">
          <Sidebar />
          <Dashboard />
          {/* You can add other components here */}
        </div>
      </div>
    </div>
  );
};

export default App;
