import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import DoughnutChart from '../Dashboard/DoughnutChart';
import axios from 'axios';

function Task() {
  const [tasks, setTasks] = useState([]);
  const [userId, setUserId] = useState(''); // State to store the user ID after login

  useEffect(() => {
    // Example: Retrieve user ID from context or local storage after login
    const loggedInUserId = localStorage.getItem('userId'); // Replace with your actual method

    if (loggedInUserId) {
      setUserId(loggedInUserId); // Set the user ID for fetching tasks
    }
  }, []);

  useEffect(() => {
    if (userId) {
      const fetchTasks = async () => {
        try {
          const response = await axios.get(`http://localhost:3001/users/${userId}/stasks`);
          if (response.status === 200) {
            setTasks(response.data.stasks);
          } else {
            console.error('Failed to fetch tasks');
          }
        } catch (error) {
          console.error('Error fetching tasks:', error);
        }
      };

      fetchTasks();
    }
  }, [userId]);

  const handleDelete = async (taskId) => {
    try {
      const response = await axios.delete(`http://localhost:3001/deleteTask/${userId}/${taskId}`);
      if (response.status === 200) {
        // Update the tasks after a successful deletion
        setTasks(tasks.filter(task => task._id !== taskId));
      } else {
        console.error('Task deletion failed');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h3>Tasks</h3>
      <DoughnutChart tasks={tasks} />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>S.no</th>
            <th>Task Name</th>
            <th>Description</th>
            <th>Status</th>
            <th>Link</th>
            <th>Completed On</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={task._id}>
              <td>{index + 1}</td>
              <td>{task.taskName}</td>
              <td>{task.description}</td>
              <td>{task.status}</td>
              <td>{task.link}</td>
              <td>{task.completedOn}</td>
              <td>
                <Button variant="danger" onClick={() => handleDelete(task._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Task;
