import { useState, useEffect } from 'react';
import axios from 'axios';

const useContent = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

    const handleLogin = async (email, password) => {
      try {
          const response = await fetch("http://localhost:3001/signin", {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify({ email, password }),
          });
  
          if (response.ok) {
              const result = await response.json();
              if (result.message === 'Sign-in successful') {
                  setUser(result.user); // Store the user object
                  setIsAuthenticated(true);
                  localStorage.setItem('isAuthenticated', true);
              } else {
                  console.error('Unexpected response:', result.message);
              }
          } else {
              console.error('HTTP error:', response.status);
              setIsAuthenticated(false);
              localStorage.removeItem('isAuthenticated');
          }
      } catch (error) {
          console.error('Error:', error);
      }
  };
  

    const handleLogout = () => {
      setUser(null)
        setIsAuthenticated(false);
        localStorage.removeItem('isAuthenticated');
    };
    useEffect(() => {
      const fetchUser = async () => {
          try {
              // Fetch user data
              const response = await axios.get('your_user_endpoint');

              if (response.status === 200) {
                  setUser(response.data); // Assuming the user data is an object
                  setIsAuthenticated(true);
              } else {
                  setIsAuthenticated(false);
                  setUser(null);
              }
          } catch (error) {
              console.error('Error fetching user:', error);
              setIsAuthenticated(false);
              setUser(null);
          }
      };

      fetchUser();
  }, []);

  return { isAuthenticated, handleLogin, handleLogout, user };
};
export default useContent;
