import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import NavBar from "./component/NavBar";
import './index.css'
import SideBar from "./component/SideBar";
import MainBar from "./component/MainBar";
import Auth from "./component/Auth";

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [sidebarItemClicked, setSidebarItemClicked] = useState(false); // State to track sidebar item clicks

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    const handleSidebarItemClick = () => {
        setSidebarItemClicked(true);
    };

    useEffect(() => {
        // Always add 'sb-nav-fixed' class to the body and conditionally add/remove 'sb-sidenav-toggled' class
        document.body.classList.toggle('sb-nav-fixed', " ");
        document.body.classList.toggle('sb-sidenav-toggled', isSidebarOpen);
    }, [isSidebarOpen]);


    const renderNavBar = () => {
        if (Location.pathname === "/auth" || sidebarItemClicked === "true") {
            return null; // Don't render NavBar on the /auth route or when sidebar item is clicked
        } else {
            return <NavBar toggleSidebar={toggleSidebar} />;
        }
    };

    return (
        <Router>
            <div>
                {renderNavBar()}
            </div>
            <div id="layoutSidenav">
                <SideBar onItemClick={handleSidebarItemClick} /> {/* Pass the handler to the Sidebar */}
                <Routes>
                    <Route path="/" element={<MainBar />} />
                </Routes>
            </div>
            <div className="bg-primary">
                <div id="layoutAuthentication">
                    <Routes>
                        <Route path="/auth" element={<Auth />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
