import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function Dbarchart({ taskData }) {
  const chartRef = useRef(null);
  const chartInstanceRef = useRef(null);

  useEffect(() => {
    if (taskData) {
      const totalTasks = 10; // Set the total number of tasks to 10
      const completedTasks = taskData.filter(task => task.status === 'completed').length;
      const balanceTasks = totalTasks - completedTasks;

      const ctx = chartRef.current.getContext('2d');
      const data = {
        labels: ['Balance Task', 'Task Completed'],
        datasets: [
          {
            label: 'Task',
            data: [balanceTasks, completedTasks], // Adjust the order to match the labels
            backgroundColor: ['#FFCA3E', '#772F67'], // Adjust the order to match the labels
            hoverOffset: 4,
          },
        ],
      };
      const config = {
        type: 'doughnut',
        data: data,
        options: {
          responsive: true,
          maintainAspectRatio: false,
        },
      };

      // Create the new Chart instance
      chartInstanceRef.current = new Chart(ctx, config);
    }

    return () => {
      // Clean up on component unmount
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [taskData]);

  return (
    <div className="card mb-4">
      <div className="card-header">
        <i className="fas fa-chart-pie me-1"></i>
        Task Status Chart
      </div>
      <div className="card-body" style={{ height: '400px' }}>
        <canvas ref={chartRef} id="doughnutChart"></canvas>
      </div>
      <div className="card-footer small text-muted">Updated yesterday at 11:59 PM</div>
    </div>
  );
}

export default Dbarchart;
