import React, { useState, useEffect } from 'react';

const TaskSubmission = () => {
  const [taskName, setTaskName] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('');
  const [completedOn, setCompletedOn] = useState('');
  const [link, setLink] = useState('');
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:3001/submitTask');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setTasks(data.tasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const taskData = {
      taskName,
      description,
      status,
      completedOn,
      link,
    };

    try {
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData),
      });

      if (response.ok) {
        console.log('Task submitted!');
        setTaskName('');
        setDescription('');
        setStatus('Pending');
        setCompletedOn(null);
        setLink('');
        fetchTasks();
      } else {
        console.error('Task submission failed!');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  const deleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3001/tasks/${taskId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Task deleted!');
        fetchTasks();
      } else {
        console.error('Task deletion failed!');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Submit a New Task</h2>
      <form onSubmit={handleSubmit}>
        {/* Form elements for task submission */}

        <button type="submit" className="btn btn-primary">Submit Task</button>
      </form>

      <h2>Tasks</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <div>
              {/* Task display details */}
              <button onClick={() => deleteTask(task.id)} className="btn btn-danger">
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskSubmission;
