import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const TaskSubmission = () => {
  const [newTaskName, setNewTaskName] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [newTaskStatus, setNewTaskStatus] = useState('');
  const [newCompletedOn, setNewCompletedOn] = useState('');
  const [newLink, setNewLink] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userId = localStorage.getItem('userId');
      const response = await fetch('http://localhost:3001/submitTask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: userId,
          day: 'New Day', // Assuming this value should be set somehow
          taskName: newTaskName,
          description: newDescription,
          link: newLink,
          status: newTaskStatus,
          completedOn: newCompletedOn,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('New task submitted:', data.newTask);
        // Additional logic can be added to update state or show a message on successful task submission
      } else {
        console.error('Task submission failed');
      }
    } catch (error) {
      console.error('Error submitting task:', error);
    }
  };

  return (
    <Container>
    <h2 className="mt-4">Task Submission</h2>
    <Form onSubmit={handleSubmit} className="mt-4">
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={newTaskName}
            onChange={(e) => setNewTaskName(e.target.value)}
            placeholder="New Task Name"
          />
        </Col>
      </Row>
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
            placeholder="Description"
          />
        </Col>
      </Row>
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={newTaskStatus}
            onChange={(e) => setNewTaskStatus(e.target.value)}
            placeholder="Status"
          />
        </Col>
      </Row>
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={newCompletedOn}
            onChange={(e) => setNewCompletedOn(e.target.value)}
            placeholder="Completed On (Date)"
          />
        </Col>
      </Row>
      <Row className="mb-3">
        <Col>
          <Form.Control
            type="text"
            value={newLink}
            onChange={(e) => setNewLink(e.target.value)}
            placeholder="Task Link"
          />
        </Col>
      </Row>
      <Button type="submit">Submit</Button>
    </Form>
  </Container>
  );
};

export default TaskSubmission;
